{
  username,
  ...
}:
{
  nix = {
    optimise.automatic = true;
    settings = {
      experimental-features = "nix-command flakes";
    };
  };

  system = {
    primaryUser = username;
    activationScripts.extraActivation.text = ''
      softwareupdate --install-rosetta --agree-to-license
    '';

    defaults = {
      # To fill gaps between windows.
      # Use BetterTouchTool to manage window tiling instead.
      WindowManager.EnableTiledWindowMargins = false;

      NSGlobalDomain = {
        ApplePressAndHoldEnabled = false;
        AppleShowAllExtensions = true;
        AppleShowAllFiles = true;
        AppleWindowTabbingMode = "always"; # for Zed "use_system_window_tabs"
        InitialKeyRepeat = 15; # shortest
        KeyRepeat = 2; # fastest
        NSAutomaticCapitalizationEnabled = false;
        NSAutomaticDashSubstitutionEnabled = false;
        NSAutomaticPeriodSubstitutionEnabled = false;
        NSAutomaticQuoteSubstitutionEnabled = false;
        "com.apple.trackpad.scaling" = 3.0;
      };

      dock = {
        autohide = true;
        show-recents = false;
        orientation = "left";
      };

      finder = {
        AppleShowAllFiles = true;
        AppleShowAllExtensions = true;
      };

      hitoolbox = {
        AppleFnUsageType = "Do Nothing";
      };

      trackpad = {
        Clicking = true;
      };
    };
  };

  homebrew = {
    enable = true;

    onActivation = {
      autoUpdate = true;
      # enable after almost all appliactions are installed by nix
      # cleanup = "zap";
    };

    casks = [
      "1password"
      "1password-cli"
      "alacritty"
      "alfred"
      "appcleaner"
      "aqua"
      "dash"
      "discord"
      "elecom-mouse-util"
      "emacs-app"
      "font-monaspace-nerd-font"
      "font-moralerspace-nf"
      "google-chrome"
      "google-japanese-ime"
      "jordanbaird-ice"
      "karabiner-elements"
      "obsidian"
      "session-manager-plugin"
      "slack"
      "visual-studio-code"
      "zed"
    ];

